def minion_game(bigstring):
    kevin = 0
    stuart = 0

    str_len = len(bigstring)

    for i in range(str_len):
        if bigstring[i] in "AEIOU":
            kevin += (str_len) - i
        else:
            stuart += (str_len) - i

    if kevin > stuart:
        print("Kevin", kevin)
    elif kevin < stuart:
        print("Stuart", stuart)
    elif kevin == stuart:
        print("Draw")
    else:
        print("Draw")


if __name__ == '__main__':
    s = ''
    minion_game(s)
